export function beautify() {
    return src(paths.html)
        .pipe(
            prettyHtml({
                indent_size: 4,
                indent_char: ' ',
                unformatted: false,
                brace_style: 'collapse'
            })
        )
        .pipe(version(versionConfig))
        .pipe(dest('dist'));
}

export function serve(done) {
    server.init({
        server: {
            baseDir: 'dist'
        },
        port: 3000
    });
    done();
}

export function serverReload(done) {
    server.reload();
    done();
}

function watchFile() {
    watch(paths.scss, scssDevelop);
    watch(paths.images, copyImages);
    watch(paths.videos, copyVideos);
    watch(
        paths.pug.watchFiles,
        series(cleanData, mergeData, buildLayouts, serverReload)
    );
    watch(
        paths.pug.data,
        series(cleanData, mergeData, buildLayouts, serverReload)
    );
    watch(paths.js, series(copyJs, serverReload));
}

const develop = series(
    cleanOutput,
    cleanData,
    mergeData,
    parallel(
        buildLayouts,
        scssDevelop,
        copyPlugin,
        copyFonts,
        copyJs,
        copyImages,
        copyVideos
    ),
    beautify,
    serve,
    watchFile
);

const build = series(
    cleanOutput,
    cleanData,
    mergeData,
    parallel(
        buildHtml,
        scssProduction,
        copyPlugin,
        copyFonts,
        copyJs,
        copyImages,
        copyVideos
    ),
    concat,
    beautify,
);

const pretty = series(beautify);
const html = series(buildHtml, beautify);

exports.watch = watchFile();
exports.html = html;
exports.pretty = pretty;
exports.build = build;
exports.default = develop;
